        return new Source("{# templates/admin/show.html.twig #}
{% extends 'base3.html.twig' %}

{% block title %}Liste des Utilisateurs{% endblock %}

{% block body %}

    <table class=\"table\">
        <thead>
            <tr>
                <th>Nom et Prenom</th>
                <th>Email</th>
                <th>Adresse</th>
                <th>Rôle</th>
                <th>Mot de passe</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
            {% for i in users %}
            <tr>
                <td>{{ i.username }}</td>
                <td>{{ i.email }}</td>
                <td>{{ i.adresse }}</td>
                <td>{{ i.roles|join(', ') }}</td>
                <td>{{ i.password }}</td>
                <td>
                    <a href=\"{{ path('app_edit', {'id': i.id}) }}\" class=\"btn btn-primary\">Modifier</a>
                    <a href=\"{{ path('app_delete', {'id': i.id}) }}\" class=\"btn btn-danger\" onclick=\"return confirm('Are you sure you want to delete this user?');\">Supprimer</a>
                </td>
            </tr>
            {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('app_user') }}\" class=\"btn btn-success\">
        Go to Another Page
    </a>
{% endblock %}", "admin/show.html.twig", "C:\\Users\\riadh.chawachi\\Desktop\\TuniVibe-abir\\templates\\admin\\show.html.twig");
